                id="description"
                name="description"
                multiline
                rows={4}
                label="Description"
                value={values.description}
                onChange={handleChange}
                autoComplete="on"
                color="secondary"
                variant="filled"
              />
              {errors.description && (
                <div className={cssClasses.error}>{errors.description}</div>
              )}
              <div className={cssClasses.controlsGroup}>
                <FormControl variant="filled">
