                  <FilledInput
                    id="priceInRupees"
                    name="priceInRupees"
                    type="text"
                    value={values.priceInRupees}
                    onChange={handleChange}
                    autoComplete="on"
                    color="secondary"
                  />
                  {errors.priceInRupees && (
                    <div className={cssClasses.error}>
                      {errors.priceInRupees}
                    </div>
                  )}
                </FormControl>
                <FormControl variant="filled">
