                <FormControl variant="filled">
                  <InputLabel htmlFor="videoURL" color="secondary">
                    Video URL
                  </InputLabel>
                  <FilledInput
                    id="videoURL"
                    name="videoURL"
                    type="text"
                    value={values.videoURL}
                    onChange={handleChange}
                    autoComplete="on"
                    color="secondary"
                  />
                  {errors.videoURL && (
                    <div className={cssClasses.error}>{errors.videoURL}</div>
                  )}
                </FormControl>
              </div>
              <FormControlLabel
                control={
                  <Switch
                    id="published"
                    name="published"
                    checked={
                      courseDetails.published ? courseDetails.published : false
                    }
                    onChange={handlePublishedFilterChange}
                    color="secondary"
                  />
                }
                label="Published"
              />

              <Button
                variant="contained"
                color="secondary"
                size="large"
                className={`${cssClasses.courseButton} ${cssClasses.formButton}`}
                onClick={editCourseHandler}
              >
                Edit
              </Button>
            </form>
          </Fragment>
        )}
        {notification}
      </main>

      {/* Footer */}
      <Footer />
    </div>
  );
};

export default EditPage;
